import React from 'react';
import { Row, Col, Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";

export default class BodyData extends React.Component {
  state = {
    query: "",
    data: [],
    filteredData: []
  };

  handleInputChange = event => {
    const query = event.target.value;

    this.setState(prevState => {
      const filteredData = prevState.data.filter(element => {
        return element.unique_name.toLowerCase().includes(query.toLowerCase());
      });

      return {
        query,
        filteredData
      };
    });
  };

  handleSubmit = event => {
    event.preventDefault();

    const DepartureCity = {
      departureCity: this.state.departureCity,
    };
    console.log(DepartureCity);
    fetch(`https://api.comparatrip.eu/cities/autocomplete/?q=`, DepartureCity)
    .then(response => response.json())
    // .then(data => this.setState({ res: data }));
    // .then(json => console.log(json))
    .then(res => {
      this.setState({
        // isModify: true,
        // departureCity : res,
        cities: res,
      })
      // console.log(this.state.cities[0].unique_name)
      console.log(this.state.departureCity)
    });
  }

  getData = () => {
    fetch(`https://api.comparatrip.eu/cities/popular/5`)
      .then(response => response.json())
      .then(data => {
        const { query } = this.state;
        const filteredData = data.filter(element => {
          return element.unique_name.toLowerCase().includes(query.toLowerCase());
        });

        this.setState({
          data,
          filteredData
        });
      });
  };

  componentWillMount() {
    this.getData();
  }

  render() {
    return (
      <Row>
      <Col md={12}>
      <div className="searchForm">
        <form onSubmit={this.handleSubmit}>
          <FormGroup controlId="departureStation" bsSize="large">
        <FormControl
        autoFocus
        type="text"
        placeholder ="Saisissez votre gare de dÃ©part..."
        value={this.state.query}
        onChange={this.handleInputChange}
        />
        </FormGroup>
        </form>
        <div>{this.state.filteredData.map(i => <p key={i.city_id}>{i.unique_name}</p>)}</div>
      </div>
      </Col>
      </Row>
    );
  }
}
