import React from 'react';
import { FormGroup, FormControl } from "react-bootstrap";

export default class BodyData extends React.Component {
  state = {
    query: "",
    filteredData: [],
    filteredData2: []
  };

  handleInputChange = event => {
    const query = event.target.value;

    this.setState(prevState => {
      const filteredData = prevState.res1.filter(element => {
        return element.unique_name.toLowerCase().includes(query.toLowerCase());
      });
      const filteredData2 = prevState.res2.filter(element => {
        return element.unique_name.toLowerCase().includes(query.toLowerCase());
      });      

      return {
        query,
        filteredData,
        filteredData2
      };
    });
  };

  handleSubmit = event => {
    event.preventDefault();

    const DepartureCity = {
      departureCity: this.state.departureCity,
    };
    console.log(DepartureCity);
    fetch(`https://api.comparatrip.eu/cities/autocomplete/?q=`, DepartureCity)
    .then(res => res.json())
    // .then(data => this.setState({ res: data }));
    // .then(json => console.log(json))
    .then(res => {
      this.setState({
        // isModify: true,
        // departureCity : res,
        cities: res,
      })
      // console.log(this.state.cities[0].unique_name)
      console.log(this.state.departureCity)
    });
  }

  getData = () => {
    const url1 = [
      `https://api.comparatrip.eu/cities/popular/5`,
    ];
    const url2 = [
      `https://api.comparatrip.eu/cities/popular/from/puteaux/5`,
    ];
    Promise.all([fetch(url1), fetch(url2)])
     .then(([res1, res2]) => { 
     return Promise.all([res1.json(), res2.json()]) 
    })
    .then(([res1, res2]) => {
      const { query } = this.state;
      const filteredData = res1.filter(element => {
        return element.unique_name.toLowerCase().includes(query.toLowerCase());
      });
      const filteredData2 = res2.filter(element => {
        return element.unique_name.toLowerCase().includes(query.toLowerCase());
      });
      this.setState({
        res1,
        res2,
        filteredData,
        filteredData2
      });
    });
  };

  UNSAFE_componentWillMount() {
    this.getData();
  }

  render() {
    return (
      <div className="BodyData">
        <p className="Title">Quel est votre trajet ?</p>
        <div className="SearchForm1">
          <form >
            <FormGroup controlId="departureStation" bsSize="large">
              <FormControl
                autoFocus
                type="text"
                placeholder ="Saisissez votre départ..."
                value={this.state.query}
                onChange={this.handleInputChange}
              />
            </FormGroup>
          </form>
          <div className="SearchData">{this.state.filteredData.map(i => <p key={i.city_id}>{i.unique_name}</p>)}</div>
        </div>
        <hr/>
        <div className="SearchForm2">
          <form onSubmit={this.handleSubmit}>
            <FormGroup controlId="arrivalStation" bsSize="large">
              <FormControl
                type="text"
                placeholder ="Saisissez votre arrivée..."
                value={this.state.query}
                onChange={this.handleInputChange}
              />
            </FormGroup>
          </form>
          <div className="SearchData">{this.state.filteredData2.map(i => <p key={i.city_id}>{i.unique_name}</p>)}</div>
        </div>
      </div>
    );
  }
}
