import React from 'react';
import { Button, FormGroup, FormControl } from "react-bootstrap";
import '../assets/css/App.css';

export default class TripForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      departureCity: "",
      password: "",
      lists: [],
    }
  }
  componentDidMount() {

  const url = [
    "https://jsonplaceholder.typicode.com/users",
  ];
  fetch(url)
  .then(response => response.json())
  .then((response) => {
    this.setState({
      users: response
    })
    console.log(this.state.users)
  });

  }
  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  handleSubmit = event => {
    event.preventDefault();

    const DepartureCity = {
      departureCity: this.state.departureCity,
  };
    fetch('https://api.comparatrip.eu/cities/autocomplete/?q=', DepartureCity)
      .then(response => response.json())
      // .then(data => this.setState({ res: data }));
      .then(json => console.log(json))
      .then(response => {
        this.setState({
          departureCity : response.departureCity
    })
    console.log(this.state.responnse)
  });
  }


  // validateForm() {
  //   return (this.state.email.length > 0 && this.state.password.length > 0);
  // }

  // handleSubmit(event) {
  //   event.preventDefault();
  // }
  render() {

  return (
    <div id="page">
      <div className="container">
        <table className="table table-striped container">
          <thead>
            <tr>
              <th scope="col">Titre</th>
              <th colSpan="1">Action</th>
              <th scope="col">Nom de l'auteur</th>
            </tr>
          </thead>
          <tbody> 
          {this.state.lists.map((list) =>
          <tr key={list.id}>
            <td>{list.unique_name}</td>

          </tr>
          )}
          </tbody> 
        </table>
        </div>    
    <div className="TripForm container">

    <div className="row">
    <div className="col-sm">
      <form /* onSubmit={handleSubmit}*/>
        <FormGroup controlId="email" bsSize="large">
          {/* <ControlLabel>Email</ControlLabel> */}
          <FormControl
            autoFocus
            type="text"
            placeholder ="Saisissez votre gare de départ..."
            value={this.state.departureCity}
            onChange={this.handleChange}
            // onChange={e => setEmail(e.target.value)}
          />
        </FormGroup>
        {/* {this.state.lists[0].unique_name} */}
        <FormGroup controlId="password" bsSize="large">
          <FormControl
          placeholder ="Saisissez votre gare d'arrivée..."
            // value={this.state.password}
            // onChange={e => setPassword(e.target.value)}
            type="button"
          />  
        </FormGroup>
        <Button block bsSize="large" /*disabled={!validateForm()} */type="submit">
          Login
        </Button>
      </form>
    </div>
    </div>
    </div>
    </div>


  );
  }
}
