import React from 'react';
import '../assets/css/App.css';
import { Link } from 'react-router-dom';

export default class Show extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      todos: [],
      id: this.props.match.params.id
    }
  }

  componentDidMount() {
    fetch(`https://jsonplaceholder.typicode.com/posts/${this.state.id}`)
    .then(res => res.json())
    .then((data) => {
      this.setState({ todos: data })
      console.log(this.state.todos)
    })
    .catch(console.log)

  }

  render() {
    return (
    <div id="post">
      <div className="container">
        <table className="table table-striped container">
          <thead>
            <tr>
              <th scope="col">Titre</th>
              <th colSpan="1">Action</th>
              <th scope="col">Nom de l'auteur</th>
            </tr>
          </thead>
          <tbody>
         {/* pas besoin d' une boucle ici */}
          <tr key={this.state.todos.id}>
            <td>{this.state.todos.title}</td>
            <td>
              <Link className="btn btn-warning" to={`/users/${this.state.todos.id}`}>
                <i className="pe-7s-pen">Voir l'auteur</i>
              </Link>
            </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
    );
  }
}
