import React from 'react';
import { Button, FormGroup, FormControl } from "react-bootstrap";
import '../assets/css/App.css';

export default class TripForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      res: []
    }
    // this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this)
  }
  componentDidMount() {
    fetch('https://api.comparatrip.eu/cities/popular/from/puteaux/5')
      .then(response => response.json())
      .then(data => this.setState({ res: data }));
  }


  // validateForm() {
  //   return (this.state.email.length > 0 && this.state.password.length > 0);
  // }

  // handleSubmit(event) {
  //   event.preventDefault();
  // }
  render() {

  return (


    <div className="TripForm container">
    <div class="row">
    <div class="col-sm">
      <form /* onSubmit={handleSubmit}*/>
        <FormGroup controlId="email" bsSize="large">
          {/* <ControlLabel>Email</ControlLabel> */}
          <FormControl
            autoFocus
            type="email"
            placeholder ="Saisissez votre gare de départ..."
            value={this.state.email}
            // onChange={e => setEmail(e.target.value)}
          />
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <FormControl
          placeholder ="Saisissez votre gare d'arrivée..."
            value={this.state.password}
            // onChange={e => setPassword(e.target.value)}
            type="password"
          />  
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <FormControl
          placeholder ="Saisissez votre gare d'arrivée..."
            value={this.state.password}
            // onChange={e => setPassword(e.target.value)}
            type="password"
          />  
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <FormControl
          placeholder ="Saisissez votre gare d'arrivée..."
            value={this.state.password}
            // onChange={e => setPassword(e.target.value)}
            type="password"
          />  
        </FormGroup>
        <Button block bsSize="large" /*disabled={!validateForm()} */type="submit">
          Login
        </Button>
      </form>
    </div>
    </div>
    </div>


  );
  }
}
