import React from 'react';
import { Row, Col, Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import '../assets/css/App.css';

export default class TripForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      departureCity: "",
      // cities:{
      //   departureCity: "",
      // },
      cities:[],
      lists: [],
      isModify: false,
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    // fetch(`https://api.unsplash.com/search/photos?page=3&query=${query}&client_id=${client_id}`)
    const url = [
      "https://api.comparatrip.eu/cities/popular/5",
    ];
    fetch(url)
    .then(response => response.json())
    .then((response) => {
      this.setState({
        lists: response
      })
      console.log(this.state.lists)
    })
    // .catch(function (error) {
    //   console.log(error);
    // });

  }
  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  handleSubmit = event => {
    event.preventDefault();

    const DepartureCity = {
      departureCity: this.state.departureCity,
    };
    console.log(DepartureCity);
    fetch(`https://api.comparatrip.eu/cities/autocomplete/?q=`, DepartureCity)
    .then(response => response.json())
    // .then(data => this.setState({ res: data }));
    // .then(json => console.log(json))
    .then(res => {
      this.setState({
        // isModify: true,
        // departureCity : res,
        cities: res,
      })
      // console.log(this.state.cities[0].unique_name)
      console.log(this.state.departureCity)
    });
  }


  // validateForm() {
  //   return (this.state.email.length > 0 && this.state.password.length > 0);
  // }

  // handleSubmit(event) {
  //   event.preventDefault();
  // }
  render() {

    return (
      <div id="page">
      <Row>
      <Col md={12}>
      <FormGroup>
      <ControlLabel>Départ</ControlLabel>
      <FormControl componentClass="select" id="region_id" value={this.state.cities.city_id} onChange={this.handleChange}>
      {this.state.lists.map((lists) => <option key={lists.city_id} value={lists.city_id}>{lists.unique_name}</option>)}
      </FormControl>
      </FormGroup>
      </Col>
      </Row>
      <div className="container">
      <table className="table table-striped container">
      <thead>
      <tr>
      <th scope="col">City</th>
      </tr>
      </thead>
      <tbody> 
      {this.state.lists.map((list) =>
        <tr key={list.city_id}>
        <td>{list.unique_name}</td>

        </tr>
        )}
        </tbody> 
        </table>
        </div>    
        <div className="TripForm container">

        <div className="row">
        <div className="col-sm">
        <form onSubmit={this.handleSubmit}>
        <FormGroup controlId="departureStation" bsSize="large">
        <FormControl
        autoFocus
        type="text"
        placeholder ="Saisissez votre gare de départ..."
        value={this.state.departureCity}
        onChange={this.handleSubmit}
        // onChange={e => setEmail(e.target.value)}
        />
        </FormGroup>
        {/* <FormGroup controlId="arrivalStation" bsSize="large">
        <FormControl
        placeholder ="Saisissez votre gare d'arrivée..."
        // value={this.state.password}
        // onChange={e => setPassword(e.target.value)}
        type="button"
        />  
      </FormGroup> */}
      <Button block bsSize="large" /*disabled={!validateForm()} */type="submit">
      Login
      </Button>
      </form>
      </div>
      </div>
      </div>
      </div>


      );
    }
  }
